import styled from 'styled-components';

export const Container = styled.div`
  width: 300px;
  height: 360px;
  background: #ffffff;
  border-radius: 10px;
  overflow: hidden;
  display: flex;
  p {
    margin: 20px;
  }
`;

export const ImgWrapper = styled.div`
  width: 260px;
  height: 280px;
  margin: 20px;
  overflow: hidden;
  border-radius: 10px;
  img {
    height: 100%;
    max-height: 100%;
    width: auto;
  }
`;
