import styled from 'styled-components';

export const Container = styled.div`
  width: 960px;
  margin: 35px auto 0;
  h2 {
    text-align: center;
    font-size: 36px;
    font-weight: normal;
  }
`;
export const Content = styled.div`
  margin-top: 45px;
  display: flex;
  justify-content: space-around;
  div {
    width: 380px;
    p {
      margin-bottom: 10px;
    }
  }
`;
