import styled from 'styled-components';

export const Container = styled.a`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
  font-size: 18px;
  color: #000;
  &:hover {
    background-color: rgba(0, 0, 0, 0.1);
  }
  p {
    margin-bottom: 10px;
  }
`;
