import styled from 'styled-components';

import Carousel from 'react-elastic-carousel';

export const Container = styled.div`
  position: relative;
  width: 980px;
  margin: 150px auto 0;
  z-index: 1;
`;

export const CrookedBG = styled.div`
  position: absolute;
  left: 0;
  height: 700px;
  width: 100%;
  background: #c4c4c4;
  transform: scale(1) rotate(-4deg) translateX(-1px) translateY(61px)
    skewX(-4deg) skewY(0deg);
`;

export const Slider = styled(Carousel).attrs({
  itemsToShow: 3,
  itemPadding: [40, 10, 0, 10],
  enableSwipe: true,
  enableMouseSwipe: true,
  showArrows: false,
  enableAutoPlay: true,
  autoPlaySpeed: 4000,
})`
  &::after {
    width: 300px;
    background: black;
    height: 300px;
  }
`;
